 jQNC = jQuery.noConflict();
jQNC.fn.sliderNav=function(options){var defaults={items:["a","b","c","d","e","f","g","h","i","j","k","l","m","n","o","p","q","r","s","t","u","v","w","x","y","z"],debug:false,height:null,arrows:true};var opts=jQNC.extend(defaults,options);var o=jQNC.meta?jQNC.extend({},opts,jQNCjQNC.data()):opts;var slider=jQNC(this);jQNC(slider).addClass('slider');jQNC('.slider-content li:first',slider).addClass('selected');jQNC(slider).append('<div class="slider-nav"><ul></ul></div>');for(var i in o.items)jQNC('.slider-nav ul',slider).append("<li><a alt='#"+o.items[i]+"'>"+o.items[i]+"</a></li>");var height=jQNC('.slider-nav',slider).height();if(o.height)height=o.height;jQNC('.slider-content, .slider-nav',slider).css('height',height);if(o.debug)jQNC(slider).append('<div id="debug">Scroll Offset: <span>0</span></div>');jQNC('.slider-nav a',slider).mouseover(function(event){var target=jQNC(this).attr('alt');var cOffset=jQNC('.slider-content',slider).offset().top;var tOffset=jQNC('.slider-content '+target,slider).offset().top;var height=jQNC('.slider-nav',slider).height();if(o.height)height=o.height;var pScroll=(tOffset-cOffset)-height/8;jQNC('.slider-content li',slider).removeClass('selected');jQNC(target).addClass('selected');jQNC('.slider-content',slider).stop().animate({scrollTop:'+='+pScroll+'px'});if(o.debug)jQNC('#debug span',slider).html(tOffset);});if(o.arrows){jQNC('.slider-nav',slider).css('top','20px');jQNC(slider).prepend('<div class="slide-up end"><span class="arrow up"></span></div>');jQNC(slider).append('<div class="slide-down"><span class="arrow down"></span></div>');jQNC('.slide-down',slider).click(function(){jQNC('.slider-content',slider).animate({scrollTop:"+="+height+"px"},500);});jQNC('.slide-up',slider).click(function(){jQNC('.slider-content',slider).animate({scrollTop:"-="+height+"px"},500);});}};